sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id; 
		$rootScope.$emit('selctNote', id);
	}
};

sp_widget.script:
(function() {
	//create an array to populate with notes
	data.notes = []; // variable data qui permet d'être utiliser sur html/serveur et client script
	var noteGR = new GlideRecord('x_snc_createnotes_note'); //ouvre table custom noteGR
	noteGR.addQuery('user', gs.getUser().getID());// pointe le champs USER de la table + valeur que doit prendre le champs
	noteGR.orderByDesc('sys_created_on');// champs sys_created_on de la table createnotes => orderbydesk => classe les champs par ordre 
	noteGR.query(); // query => effectue les requêtes
	while (noteGR.next()) { //itère sur les résultats
		var noteObj = {}; // création d'objet avce les {}
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//get the first 20 characters of the description => slice
		noteObj.note = noteGR.getValue('note').slice(0,20); // création d'1 champs grâce au point dans 'objet noteGR
		//push the populated obj into the array
		data.notes.push(noteObj); // méthode push pour inclure les valeurs des instructions 12/13
	}
})();
